CREATE FUNCTION mycas.adatta_my_portal_pf_abilitazione() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE

	p_id_pf_ruolo_myportal myportal.pf_ruolo.id_pf_ruolo%TYPE;
	p_id_pf_ruolo_mycas mycas.pf_ruolo.id_pf_ruolo%TYPE;

