	set id_pf_ruolo = p_id_pf_ruolo_mycas
	where id_pf_ruolo = p_id_pf_ruolo_myportal;
	
	-- aziende
	
	select id_pf_ruolo
	into p_id_pf_ruolo_myportal
	from myportal.pf_ruolo
	where cod_gruppo = 'AZIENDE';
	
	select id_pf_ruolo
	into p_id_pf_ruolo_mycas
	from mycas.pf_ruolo
	where ruolo = 'AZIENDE';
	
	update myportal.pf_abilitazione
	set id_pf_ruolo = p_id_pf_ruolo_mycas
	where id_pf_ruolo = p_id_pf_ruolo_myportal;
	
	-- coordinatore
	
	select id_pf_ruolo
	into p_id_pf_ruolo_myportal
	from myportal.pf_ruolo
	where cod_gruppo = 'COORDINA';
	
	select id_pf_ruolo
	into p_id_pf_ruolo_mycas
	from mycas.pf_ruolo
	where ruolo = 'COORDINA';
	
	update myportal.pf_abilitazione
	set id_pf_ruolo = p_id_pf_ruolo_mycas
	where id_pf_ruolo = p_id_pf_ruolo_myportal;
	
	-- consulente
	
	select id_pf_ruolo
	into p_id_pf_ruolo_myportal
	from myportal.pf_ruolo
	where cod_gruppo = 'CONSULEN';
	
	select id_pf_ruolo
	into p_id_pf_ruolo_mycas
	from mycas.pf_ruolo
	where ruolo = 'CONSULEN';
	
	update myportal.pf_abilitazione
	set id_pf_ruolo = p_id_pf_ruolo_mycas
	where id_pf_ruolo = p_id_pf_ruolo_myportal;
	
	-- province
	
	select id_pf_ruolo
	into p_id_pf_ruolo_myportal
	from myportal.pf_ruolo
	where cod_gruppo = 'PROV_PG';
	
	select id_pf_ruolo
	into p_id_pf_ruolo_mycas
	from mycas.pf_ruolo
	where ruolo = 'PROV';
	
	update myportal.pf_abilitazione
	set id_pf_ruolo = p_id_pf_ruolo_mycas
	where id_pf_ruolo = p_id_pf_ruolo_myportal;
	
	-- system
	
	select id_pf_ruolo
	into p_id_pf_ruolo_myportal
	from myportal.pf_ruolo
	where cod_gruppo = 'SYSTEM';
	
	select id_pf_ruolo
	into p_id_pf_ruolo_mycas
	from mycas.pf_ruolo
	where ruolo = 'SYSTEM';
	
	update myportal.pf_abilitazione
	set id_pf_ruolo = p_id_pf_ruolo_mycas
	where id_pf_ruolo = p_id_pf_ruolo_myportal;
	
	-- pulizia finale
	
	delete from myportal.pf_abilitazione
	where id_pf_ruolo not in (
		select id_pf_ruolo
		from mycas.pf_ruolo
	);
	
	RAISE NOTICE 'FINE Popolamento pf_abilitazione';
	
END;
$$;


