CREATE FUNCTION mycas.create_profil_sare() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  p_ruolo_princ mycas.pf_ruolo_principal%ROWTYPE;
  r_count INTEGER;
  ruolo_funz_id mycas.pf_gruppo_funzione.id_pf_gruppo_funzione%TYPE;
  p_gruppo_funzione mycas.pf_gruppo_funzione%ROWTYPE;
  
BEGIN
  RAISE NOTICE 'Inizio porting di pf_profilatura';

  r_count:= 0;

  FOR p_ruolo_princ IN select rp.* 
			from mycas.pf_ruolo_principal rp
			inner join mycas.pf_principal p on p.id_pf_principal = rp.id_pf_principal
			inner join mycas.azienda_info i on i.id_pf_principal = rp.id_pf_principal
			where id_pf_ruolo = 3
			and flag_abilitato_sare = 'Y' and i.cod_autorizzazione_sare = '4' LOOP
    -- conto i record processati
    r_count := r_count + 1;

    IF (r_count % 1000) = 0 THEN
      RAISE NOTICE 'Record processati: %', r_count;
    END IF;

    FOR p_gruppo_funzione IN SELECT pgf.* FROM mycas.pf_gruppo_funzione pgf WHERE cod_tipo_gruppo = 'AZIENDE'  LOOP
     	
	    INSERT INTO mycas.pf_profilatura(
		    id_pf_gruppo_funzione, id_pf_principal, flg_inserimento, 
		    dtm_ins, dtm_mod, id_principal_ins, id_principal_mod, flg_gestisci, 
		    flg_visualizza)
	    VALUES (p_gruppo_funzione.id_pf_gruppo_funzione, p_ruolo_princ.id_pf_principal, 'N', 
		    '2013-01-01', '2013-01-01', 0, 0, p_gruppo_funzione.flg_gestisci, 
		    p_gruppo_funzione.flg_visualizza);

    END LOOP;		

  END LOOP;

  RAISE NOTICE 'Fine porting di pf_profilatura';
END;
$$;


