-- Name: create_profilatura(); Type: FUNCTION; Schema: mycas; Owner: myportal
--

CREATE FUNCTION mycas.create_profilatura() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  gruppo            mycas.pf_gruppo.cod_tipo_gruppo%TYPE;
  gruppoPrec        mycas.pf_gruppo.cod_tipo_gruppo%TYPE;
  principal         mycas.pf_principal%ROWTYPE;
  r_count_tot       INTEGER;
  r_count_gruppo    INTEGER;
  p_gruppo_funzione mycas.pf_gruppo_funzione%ROWTYPE;
  concessaSare      INTEGER;
  
BEGIN
  RAISE NOTICE 'Inizio popolamento di pf_profilatura';

  r_count_tot:= 0;
  r_count_gruppo:= 0;
  gruppo:= '';
  gruppoPrec:= '';
  /* Verifica per quali tipi di gruppo sono censite delle funzioni da profilare ( primo cliclo FOR )  
     Per oguno di questi tipi gruppo si vanno a recuperare i rispettivi utenti appartenenti a quel gruppo ( Secondo FOR )
  */
  FOR gruppo IN select cod_tipo_gruppo from mycas.pf_gruppo_funzione gf group by cod_tipo_gruppo 
  LOOP
      /*  Azzera il conteggio per ogni gruppo */
      IF gruppo != gruppoPrec THEN 
        r_count_gruppo := 0;
      END IF;
      
      FOR principal IN   select *
                           from mycas.pf_principal p 
                     inner join mycas.pf_principal_gruppo pg on p.id_pf_priNcipal = pg.id_pf_priNcipal
                     inner join mycas.pf_gruppo g            on pg.cod_gruppo = g.cod_gruppo
                     where g.cod_tipo_gruppo = gruppo
                       and flag_abilitato_sare = 'Y' 
      LOOP
        -- Conto i record processati
        r_count_tot := r_count_tot + 1;

        IF (r_count_tot % 100) = 0 THEN
          RAISE NOTICE 'Record processati: %  -> Tipo gruppo: %', r_count_tot, gruppo;
        END IF;
        
        /*  Una volta recuperati gli utenti di un dato gruppo, si recuperano le funzioni associate a quel gruppo dalla tabella pf_gruppo_funzione (Ciclo FOR sotto ).
            Si inserisce la profilatura della funzione abilitando tutti i permessi che sono consentiti per quella funzione. 
             Es. se per la funzione "003" si può usare sia il flg_gestisci che il flg_visualizza mettero nella pf_rpofilatura di quell'utente
                flg_gestisci = 'Y' e flg_visulaizza = 'Y'
             Se per la funzione "004" NON si può usare il flg_gestisci ma solo il flg_visualizza mettero' nella pf_rpofilatura di quell'utente
                flg_gestisci = 'N' e flg_visulaizza = 'Y' 
             In termini pratici copio i valori dei campi flg_gestisci e flg_visualizza e li metto nella pf_profilatura per quell'utente.
        */
        IF (gruppo = 'AZIENDE') THEN 
            /*  In caso di utente azienda verifico che oltre ad essere abilitato a SARE abbia lo stato della richiesta valorizzato a "Concessa"  */
            select count(1) INTO concessaSare  
              from mycas.pf_principal p
             inner join mycas.azienda_info i on i.id_pf_principal = p.id_pf_principal
             where p.id_pf_principal = principal.id_pf_principal
              and i.cod_autorizzazione_sare = '4'; /* 4 = Concessa */ 
        ELSE /* Se non è AZIENDA valorizzo il concessaSARE = 1 in modo da "bypassare" il controllo e non dover scrivere due volte lo stesso cliclo*/
            concessaSare := 1;
        END IF; /* IF (gruppo = 'AZIENDE') THEN  */
        
        IF ( concessaSare>0 ) THEN
            FOR p_gruppo_funzione IN SELECT pgf.* FROM mycas.pf_gruppo_funzione pgf WHERE cod_tipo_gruppo = gruppo  
            LOOP
                
                INSERT INTO mycas.pf_profilatura(
                    id_pf_gruppo_funzione, id_pf_principal, flg_inserimento, 
                    dtm_ins, dtm_mod, id_principal_ins, id_principal_mod, flg_gestisci, 
                    flg_visualizza)
                VALUES (p_gruppo_funzione.id_pf_gruppo_funzione, principal.id_pf_principal, null, 
                    '2013-01-01', '2013-01-01', 0, 0, p_gruppo_funzione.flg_gestisci, 
                    p_gruppo_funzione.flg_visualizza);
                -- Conto i record inseriti x gruppo 
                r_count_gruppo := r_count_gruppo +1;

                END LOOP; /* FOR p_gruppo_funzione IN SELECT pgf.* FROM mycas.pf_gruppo_funzione pgf WHERE cod_tipo_gruppo = gruppo */       
        END IF; /* IF ( concessaSare>0 ) THEN */

        concessaSare := 0;

        END LOOP; /* FOR principal IN   select * .... */
  
        RAISE NOTICE '#### Record inseriti nel gruppo:  %  =  %', gruppo, r_count_gruppo;

  END LOOP; /* FOR gruppo IN select cod_tipo_gruppo from mycas.pf_gruppo_funzione gf group by cod_tipo_gruppo */

  RAISE NOTICE 'Fine popolamento di pf_profilatura';
END;
$$;


