--

CREATE FUNCTION mycas.popola_pf_gruppo_pf_principal_gruppo() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  
  utente RECORD;
  
  r_count INTEGER;
  
  system_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  citt_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  aziende_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  prov_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  consulen_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  coordina_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  
  -- pf_gruppo
  p_cod_gruppo mycas.pf_gruppo.cod_gruppo%TYPE;
  p_cod_tipo_gruppo mycas.pf_gruppo.cod_tipo_gruppo%TYPE;
  p_descrizione_gruppo mycas.pf_gruppo.descrizione%TYPE;
  
  -- pf_principal
  p_nome mycas.pf_principal.nome%type;
  p_cognome mycas.pf_principal.cognome%type;
  
  -- pf_principal_gruppo
  p_flag_amministratore mycas.pf_principal_gruppo.flag_amministratore%TYPE;
  
  -- azienda_info
  p_codice_fiscale_azienda mycas.azienda_info.codice_fiscale%TYPE;
  p_ragione_sociale_azienda mycas.azienda_info.ragione_sociale%TYPE;
  p_cod_tipo_utente_sare_azienda mycas.azienda_info.cod_tipo_utente_sare%TYPE;
  
  -- provincia
  p_cod_tipo_utente_sare_provincia mycas.provincia.cod_tipo_utente_sare%TYPE;
  
  -- de_provincia
  p_denominazione_provincia mycas.de_provincia.denominazione%TYPE;
  
  -- utente_info
  p_codice_fiscale_cittadino mycas.utente_info.codice_fiscale%TYPE;
  
BEGIN

  RAISE NOTICE 'Inizio popolamento di mycas.pf_gruppo e mycas.pf_principal_gruppo';

  r_count:= 0;

  -- prendo gli id dei ruoli
  SELECT id_pf_ruolo INTO system_id FROM mycas.pf_ruolo WHERE ruolo = 'SYSTEM';
  SELECT id_pf_ruolo INTO citt_id FROM mycas.pf_ruolo WHERE ruolo = 'CITT';
  SELECT id_pf_ruolo INTO aziende_id FROM mycas.pf_ruolo WHERE ruolo = 'AZIENDE';
  SELECT id_pf_ruolo INTO prov_id FROM mycas.pf_ruolo WHERE ruolo = 'PROV';
  SELECT id_pf_ruolo INTO consulen_id FROM mycas.pf_ruolo WHERE ruolo = 'CONSULEN';
  SELECT id_pf_ruolo INTO coordina_id FROM mycas.pf_ruolo WHERE ruolo = 'COORDINA';
  
  -- processing ruolo azienda
  

  FOR utente IN 
	
	SELECT p.id_pf_principal, r.id_pf_ruolo, p.flag_abilitato_sare, p.nome, p.cognome
	FROM mycas.pf_principal p 
	JOIN mycas.pf_ruolo_principal rp ON p.id_pf_principal = rp.id_pf_principal 
	JOIN mycas.pf_ruolo r ON rp.id_pf_ruolo = r.id_pf_ruolo 
	
  LOOP
	
	-------------------------------
    -- conta i record processati --
    -------------------------------
	
	r_count := r_count + 1;
    IF (r_count % 1000) = 0 THEN
      RAISE NOTICE 'Record processati: %', r_count;
    END IF;

	p_descrizione_gruppo := 'descrizione nulla';
	p_nome = utente.nome;
	p_cognome = utente.cognome;
	
	IF (p_nome IS NULL) THEN
		p_nome := ' ';
	END IF;
	
	IF (p_cognome IS NULL) THEN
		p_cognome := ' ';
	END IF;
	
	--------------------------------
	-- gestione utenti di sistema --
	--------------------------------
	
	IF (utente.id_pf_ruolo = system_id) THEN
		
		p_cod_tipo_gruppo := 'SYSTEM';
		p_descrizione_gruppo := 'Utente di sistema';
		p_flag_amministratore := 'Y';
		
	END IF;
	
	----------------------
	-- gestione aziende --
	----------------------
	
	IF (utente.id_pf_ruolo = aziende_id) THEN
		
		SELECT azienda.codice_fiscale, azienda.ragione_sociale, azienda.cod_tipo_utente_sare, tipoutentesare.cod_tipo_gruppo
		INTO p_codice_fiscale_azienda, p_ragione_sociale_azienda, p_cod_tipo_utente_sare_azienda, p_cod_tipo_gruppo
		FROM myportal.azienda_info azienda
		INNER JOIN mycas.de_tipo_utente_sare tipoutentesare on tipoutentesare.cod_tipo_utente_sare = azienda.cod_tipo_utente_sare
		WHERE id_pf_principal = utente.id_pf_principal;

		IF (p_codice_fiscale_azienda IS NULL) THEN
			p_codice_fiscale_azienda := ' ';
		END IF;
		
		IF (p_ragione_sociale_azienda IS NULL) THEN
			p_ragione_sociale_azienda := ' ';
		END IF;
		
		p_descrizione_gruppo := 'Azienda - CF: ' || p_codice_fiscale_azienda || ' Rag. sociale: ' || p_ragione_sociale_azienda;
		p_flag_amministratore := 'Y';
		
		IF (utente.flag_abilitato_sare = 'N') THEN
			p_cod_tipo_gruppo := 'AZIENDE';
		ELSIF (utente.flag_abilitato_sare = 'Y') THEN
			-- ok quello preso dalla de_tipo_utente_sare nella query precedente
		END IF;

	END IF;
	
	-----------------------
	-- gestione province --
	-----------------------
	
	IF (utente.id_pf_ruolo = prov_id) THEN
		
		SELECT provincia.cod_tipo_utente_sare, deprovincia.denominazione
		INTO p_cod_tipo_utente_sare_provincia, p_denominazione_provincia
		FROM mycas.provincia provincia
		INNER JOIN mycas.de_provincia deprovincia on deprovincia.cod_provincia = provincia.cod_provincia
		WHERE id_pf_principal = utente.id_pf_principal;
		
		IF (p_denominazione_provincia IS NULL) THEN
			p_denominazione_provincia := ' ';
		END IF;
		
		p_cod_tipo_gruppo := 'PROV';
		p_descrizione_gruppo := 'Operatore CPI della provincia di ' || p_denominazione_provincia || ' (descrizione temporanea)';
		p_flag_amministratore := 'Y';
		
		IF (p_cod_tipo_utente_sare_provincia = 'C') THEN
			p_cod_tipo_gruppo := 'CPI';
			p_descrizione_gruppo := 'Operatore CPI della provincia di ' || p_denominazione_provincia;
			p_flag_amministratore := 'Y';
		ELSIF (p_cod_tipo_utente_sare_provincia = 'G') THEN
			p_cod_tipo_gruppo := 'CIGSARE';
			p_descrizione_gruppo := 'Controller CIG';
			p_flag_amministratore := 'N';
		ELSIF (p_cod_tipo_utente_sare_provincia = 'P') THEN
			p_cod_tipo_gruppo := 'PROV';
			p_descrizione_gruppo := 'Super user SARE della provincia di ' || p_denominazione_provincia;
			p_flag_amministratore := 'Y';
		ELSE
			RAISE NOTICE 'Errore: provincia con cod_tipo_utente_sare NULLO (id_pf_principal = %)', utente.id_pf_principal;
		END IF;
		
	END IF;
	
	------------------------
	-- gestione cittadino --
	------------------------
	
	IF (utente.id_pf_ruolo = citt_id) THEN
		
		SELECT codice_fiscale
		INTO p_codice_fiscale_cittadino
		FROM mycas.utente_info
		WHERE id_pf_principal = utente.id_pf_principal;
		
		IF (p_codice_fiscale_cittadino IS NULL) THEN
			p_codice_fiscale_cittadino := ' ';
		END IF;
		
		p_cod_tipo_gruppo := 'CITT';
		p_descrizione_gruppo := 'Cittadino - Nome: ' || p_nome || '; Cognome: ' || p_cognome || '; CF: ' || p_codice_fiscale_cittadino;
		p_flag_amministratore := 'N';
		
	END IF;
	
	---------------------------
	-- gestione coordinatore --
	---------------------------
	
	IF (utente.id_pf_ruolo = coordina_id) THEN
		
		p_cod_tipo_gruppo := 'COORDINA';
		p_descrizione_gruppo := 'Coodinatore atipici ' || p_nome || ' - ' || p_cognome;
		p_flag_amministratore := 'N';
		
	END IF;
	
	-------------------------
	-- gestione consulente --
	-------------------------
	
	IF (utente.id_pf_ruolo = consulen_id) THEN
		
		p_cod_tipo_gruppo := 'CONSULEN';
		p_descrizione_gruppo := 'Consulente ' || p_nome || ' - ' || p_cognome;
		p_flag_amministratore := 'N';
		
	END IF;

	----------------
    -- debug info --
	----------------
	
	IF (p_descrizione_gruppo is null) THEN
		RAISE NOTICE 'descrizione gruppo nullo.. id_pf_principal = % id_pf_ruolo: %', utente.id_pf_principal, utente.id_pf_ruolo;
	END IF;
	
	IF (p_cod_tipo_gruppo is null) THEN
		RAISE NOTICE 'codtipogruppo nullo.. id_pf_principal = % id_pf_ruolo: %', utente.id_pf_principal, utente.id_pf_ruolo;
	END IF;
	
	------------------------------------------
    -- crea l'associazione principal-gruppo --
	------------------------------------------
	
	if (p_cod_tipo_gruppo is not null) then
	
		p_cod_gruppo := nextval('mycas.pf_gruppo_cod_gruppo_seq');
		
	    INSERT INTO mycas.pf_gruppo (
			cod_gruppo, 
			cod_tipo_gruppo, 
			descrizione, 
			dtm_ins, 
			dtm_mod
		) VALUES (
			p_cod_gruppo,
			p_cod_tipo_gruppo,
			p_descrizione_gruppo,
			current_date,
			current_date
		);
		
	    INSERT INTO mycas.pf_principal_gruppo (
			id_pf_principal_gruppo, 
			id_pf_principal, 
			cod_gruppo, 
			flag_amministratore,
			dtm_ins, 
			dtm_mod, 
			id_principal_ins, 
			id_principal_mod
		) VALUES (
	      nextval('mycas.pf_principal_gruppo_id_pf_principal_gruppo_seq'),
	      utente.id_pf_principal,
	      p_cod_gruppo,
		  p_flag_amministratore,
	      current_date,
	      current_date,
	      0,
	      0
		);
	    
		IF (utente.id_pf_ruolo = aziende_id) THEN
			-- copio l'azienda da myportal a mycas
			INSERT INTO mycas.azienda_info 
