CREATE FUNCTION mycas.popola_pf_gruppo_pf_principal_gruppo_per_provincia() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  utente RECORD;
  r_count INTEGER;
  new_cod_gruppo INTEGER;
  p_nome mycas.pf_principal.nome%type;
  p_cognome mycas.pf_principal.cognome%type;

BEGIN
  RAISE NOTICE 'Inizio popolamento di mycas.pf_gruppo e mycas.pf_principal_gruppo per provincia';

  r_count:= 0;

  FOR utente IN 
    SELECT p.id_pf_principal, prov.id_provincia, p.flag_abilitato_sare, p.nome, p.cognome
    FROM mycas.pf_principal p 
    JOIN mycas.provincia prov ON p.id_pf_principal = prov.id_pf_principal
  LOOP

    -------------------------------
    -- conta i record processati --
    -------------------------------
    r_count := r_count + 1;
    IF (r_count % 1000) = 0 THEN
      RAISE NOTICE 'Record processati: %', r_count;
    END IF;

    p_nome = utente.nome;
    p_cognome = utente.cognome;

    IF (p_nome IS NULL) THEN
      p_nome := ' ';
    END IF;
    IF (p_cognome IS NULL) THEN
      p_cognome := ' ';
    END IF;


    SELECT nextval('mycas.pf_gruppo_cod_gruppo_seq') INTO new_cod_gruppo;

    INSERT INTO mycas.pf_gruppo (
      cod_gruppo,
      cod_tipo_gruppo,
      descrizione,
      dtm_ins,
      dtm_mod,
      id_principal_ins,
      id_principal_mod
    ) VALUES (
      new_cod_gruppo,
      'PROV',
      'Provincia - ' || p_nome || ' ' || p_cognome,
      current_date,
      current_date,
      0,
      0
    );

    INSERT INTO mycas.pf_principal_gruppo (
      id_pf_principal_gruppo, 
      id_pf_principal, 
      cod_gruppo, 
      flag_amministratore,
      dtm_ins, 
      dtm_mod, 
      id_principal_ins, 
      id_principal_mod
    ) VALUES (
      nextval('mycas.pf_principal_gruppo_id_pf_principal_gruppo_seq'),
      utente.id_pf_principal,
      new_cod_gruppo,
      'Y',
      current_date,
      current_date,
      0,
      0);

    UPDATE mycas.provincia SET cod_gruppo = new_cod_gruppo WHERE id_provincia = utente.id_provincia;
  END LOOP;

  RAISE NOTICE 'Fine popolamento di mycas.pf_gruppo e mycas.pf_principal_gruppo per provincia';
END;
$$;


