--

CREATE FUNCTION mycas.populate_default_azienda() RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
  id_utente INTEGER;
  id_ruolo_gruppo INTEGER;
BEGIN
  FOR id_utente IN (select id_pf_principal from mycas.azienda_info)
    LOOP
      FOR id_ruolo_gruppo IN (
        select rg.id_gp_ruolo_gruppo 
	from mycas.gp_ruolo_gruppo rg
	inner join mycas.gp_gruppo g ON rg.id_gp_gruppo = g.id_gp_gruppo
	inner join mycas.gp_de_tipo_gruppo dtg ON g.cod_tipo_gruppo = dtg.cod_tipo_gruppo
	inner join mycas.gp_de_macro_tipo dmt ON dtg.cod_macro_tipo = dmt.cod_macro_tipo
	where rg.flg_default = TRUE and dmt.cod_macro_tipo = 'AZI')
	LOOP
          INSERT INTO mycas.gp_profilatura(id_pf_principal, id_gp_ruolo_gruppo, dtm_ins, dtm_mod, id_principal_ins, id_principal_mod)
	  VALUES (id_utente, id_ruolo_gruppo, now(), now(), 0, 0);
	END LOOP;
    END LOOP;
    RETURN TRUE;
END;
$$;


