  old_consulen_id myportal.pf_ruolo.id_pf_ruolo%TYPE;
  old_coordina_id myportal.pf_ruolo.id_pf_ruolo%TYPE;
  system_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  citt_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  aziende_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  prov_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  consulen_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
  coordina_id mycas.pf_ruolo.id_pf_ruolo%TYPE;
BEGIN 
  RAISE NOTICE 'Inizio porting di myportal.pf_ruolo_principal';

  r_count:= 0;

  -- prendo i vecchi id dei ruoli
  SELECT id_pf_ruolo INTO old_system_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'SYSTEM';
  SELECT id_pf_ruolo INTO old_citt_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'CITT';
  SELECT id_pf_ruolo INTO old_aziende_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'AZIENDE';
  SELECT id_pf_ruolo INTO old_prov_bo_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_BO';
  SELECT id_pf_ruolo INTO old_prov_fc_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_FC';
  SELECT id_pf_ruolo INTO old_prov_fe_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_FE';
  SELECT id_pf_ruolo INTO old_prov_mo_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_MO';
  SELECT id_pf_ruolo INTO old_prov_pc_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_PC';
  SELECT id_pf_ruolo INTO old_prov_pr_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_PR';
  SELECT id_pf_ruolo INTO old_prov_ra_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_RA';
  SELECT id_pf_ruolo INTO old_prov_re_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_RE';
  SELECT id_pf_ruolo INTO old_prov_rn_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_RN';
  
  SELECT id_pf_ruolo INTO old_prov_pg_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_PG';
  SELECT id_pf_ruolo INTO old_prov_tr_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_TR';
  
  SELECT id_pf_ruolo INTO old_prov_tn_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'PROV_TN';
  
  SELECT id_pf_ruolo INTO old_consulen_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'CONSULEN';
  SELECT id_pf_ruolo INTO old_coordina_id FROM myportal.pf_ruolo WHERE cod_gruppo = 'COORDINA';

  -- prendo i nuovi id dei ruoli
  SELECT id_pf_ruolo INTO system_id FROM mycas.pf_ruolo WHERE ruolo = 'SYSTEM';
  SELECT id_pf_ruolo INTO citt_id FROM mycas.pf_ruolo WHERE ruolo = 'CITT';
  SELECT id_pf_ruolo INTO aziende_id FROM mycas.pf_ruolo WHERE ruolo = 'AZIENDE';
  SELECT id_pf_ruolo INTO prov_id FROM mycas.pf_ruolo WHERE ruolo = 'PROV';
  SELECT id_pf_ruolo INTO consulen_id FROM mycas.pf_ruolo WHERE ruolo = 'CONSULEN';
  SELECT id_pf_ruolo INTO coordina_id FROM mycas.pf_ruolo WHERE ruolo = 'COORDINA';

  FOR p_ruolo_principal IN SELECT * FROM myportal.pf_ruolo_principal ORDER BY id_pf_ruolo_principal ASC LOOP
    -- conto i record processati
    r_count := r_count + 1;

    IF (r_count % 1000) = 0 THEN
      RAISE NOTICE 'Record processati: %', r_count;
    END IF;

    -- all'ultima iterazione conterra' l'ultimo id usato. Serve per settare la nuova sequence
    last_id_pf_ruolo_principal := p_ruolo_principal.id_pf_ruolo_principal;

