          WHEN p_ruolo_principal.id_pf_ruolo = old_aziende_id THEN aziende_id
          WHEN p_ruolo_principal.id_pf_ruolo IN (old_prov_bo_id, old_prov_fc_id, old_prov_fe_id, old_prov_mo_id, old_prov_pc_id, old_prov_pr_id, old_prov_ra_id, old_prov_re_id, old_prov_rn_id, old_prov_pg_id, old_prov_tr_id, old_prov_tn_id) THEN prov_id
          WHEN p_ruolo_principal.id_pf_ruolo = old_consulen_id THEN consulen_id
          WHEN p_ruolo_principal.id_pf_ruolo = old_coordina_id THEN coordina_id
    END,
    p_ruolo_principal.id_pf_principal, 
    p_ruolo_principal.dtm_ins, 
    p_ruolo_principal.dtm_mod, 
    p_ruolo_principal.id_principal_ins, 
    p_ruolo_principal.id_principal_mod);

  END LOOP;

  -- configuro la nuova sequence per riprendere da dove ha finito la vecchia
  PERFORM setval('mycas.pf_ruolo_principal_id_pf_ruolo_principal_seq', last_id_pf_ruolo_principal);

  RAISE NOTICE 'Sequence: mycas.pf_ruolo_principal_id_pf_ruolo_principal_seq settata a %', last_id_pf_ruolo_principal;

  RAISE NOTICE 'Fine porting di myportal.pf_ruolo_principal';
END;
$$;


