--

CREATE FUNCTION myportal.ins_portlet_ido_cv() RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
 row RECORD;
 id_portlet INTEGER;
 old_id_portlet INTEGER;
 
BEGIN
	-- ID della vecchia portlet Curriculum Vitae
	SELECT id_pt_portlet into old_id_portlet FROM myportal.pt_portlet
	WHERE nome = '_portlet_curricula';
	
	SELECT id_pt_portlet into id_portlet FROM myportal.pt_portlet
	WHERE nome = '_portlet_curricula_ido';

	IF (id_portlet IS NOT NULL) THEN
		FOR row in
	 	-- La inner join serve a non rompere il reset della pt_scrivania, quindi l'insert bisognerebbe farlo solo per gli utenti che abbiano almeno una portlet nella loro scrivania
		SELECT DISTINCT utente.id_pf_principal
		FROM mycas.utente_info utente
		INNER JOIN myportal.pt_scrivania sc ON sc.id_pf_principal = utente.id_pf_principal
		WHERE NOT EXISTS (
			SELECT *
			FROM myportal.pt_scrivania sc2
			WHERE sc2.id_pf_principal = utente.id_pf_principal
			AND sc2.id_pt_portlet = id_portlet )
	   	LOOP
    		INSERT INTO myportal.pt_scrivania(id_pt_portlet, id_pf_principal, posizione, opt_colonna, 
            	flag_visualizza, flag_ridotta, dtm_ins, dtm_mod, id_principal_ins, id_principal_mod)
    		VALUES (id_portlet, row.id_pf_principal,1,'R','Y','N',now(),now(),0,0);
	   	END LOOP;
	   	
		IF (old_id_portlet IS NOT NULL) THEN
			DELETE FROM myportal.pt_scrivania WHERE id_pt_portlet = old_id_portlet;
		END IF;
	   	
	ELSE
		RAISE NOTICE 'ATTENZIONE: non e'' stata trovata la portlet specifica _portlet_curricula_ido %', id_portlet;		
	END IF;
	
	RETURN TRUE;
   
END;
$$;


