--

CREATE FUNCTION myportal.popolamento_yg_adesione_cpi_assegnazione(in_cod_regione_portale character varying) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  
  p_record_yg_adesione myportal.yg_adesione%ROWTYPE;
  p_cod_regione_utente myportal.de_regione.cod_regione%TYPE;
  p_cod_cpi_utente myportal.de_cpi.cod_cpi%TYPE;
  
  p_count INTEGER;
  
BEGIN

  RAISE NOTICE 'Inizio popolamento campo cod_cpi_assegnazione tabella yg_adesione';

  p_count:= 0;

  FOR p_record_yg_adesione IN 
	
	SELECT * 
	FROM myportal.yg_adesione
	WHERE cod_mono_prov = 'I' 
	ORDER BY id_pf_principal ASC 
	
  LOOP

    -- conto i record processati
    p_count := p_count + 1;

    IF (p_count % 1000) = 0 THEN
      RAISE NOTICE 'Record processati: %', p_count;
    END IF;

    -- regione del lavoratore basata sul comune domicilio
    select prov.cod_regione, comdom.cod_cpi
    into p_cod_regione_utente, p_cod_cpi_utente
    from myportal.de_provincia prov
    inner join myportal.de_comune comdom on comdom.cod_provincia = prov.cod_provincia
    inner join myportal.utente_info utente on utente.cod_com_domicilio = comdom.cod_com
    where utente.id_pf_principal = p_record_yg_adesione.id_pf_principal;

    IF ((in_cod_regione_portale IS NOT NULL) and 
        (p_cod_regione_utente IS NOT NULL) and
         p_cod_regione_utente = in_cod_regione_portale) THEN

	UPDATE myportal.YG_ADESIONE
	SET COD_CPI_ASSEGNAZIONE = p_cod_cpi_utente
	WHERE ID_PF_PRINCIPAL = p_record_yg_adesione.id_pf_principal;

    ELSE

	UPDATE myportal.YG_ADESIONE
	SET COD_CPI_ASSEGNAZIONE = COD_CPI_ADESIONE
	WHERE ID_PF_PRINCIPAL = p_record_yg_adesione.id_pf_principal;
    
    END IF;

  END LOOP;

  RAISE NOTICE 'Record processati: %', p_count;
  RAISE NOTICE 'Fine popolamento campo cod_cpi_assegnazione tabella yg_adesione';
  
END;
$$;


