-- Name: update_cv_dati_personali(integer); Type: FUNCTION; Schema: myportal; Owner: myportal
--

CREATE FUNCTION myportal.update_cv_dati_personali(daylimit integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
    declare
       
        var_dte date;        
        p_record_cv_dati_personali RECORD;
        p_record_cv_istruzione RECORD;
        p_record_cv_esperienze RECORD;
        p_record_de_titolo RECORD;
        p_record_de_bp_mansione RECORD;
        p_record_cv_competenze_trasv RECORD;
        p_record_cv_professioni_desid RECORD;
        p_record_de_bp_mansione_prof_desid RECORD;
        p_count INTEGER;
        p_id_cv_dati_personali INTEGER;
        p_specifica text;
        p_cod_titolo text; 
        p_cod_titolo_padre text;
        p_cod_mansione_istat text;
        p_professione text;
        p_descrizione text;
        p_cap_comp_rel_interpersonali text;
        p_cap_comp_tecniche text;
        p_cap_comp_organizzative text;
        p_data timestamp;
        p_cod_mansione_istat_esp_desid text;
        p_descrizione_professione_desid text;
        p_ret_prof INTEGER;
        p_ret_esp_ND INTEGER;
        p_ret_esp INTEGER;
        p_ret_istr INTEGER;
        p_record_cv_formazione RECORD;
        p_record_cv_lingua RECORD;
        p_numAnno INTEGER;
        p_codGradoLetto text;
        p_codGradoScritto text;
        p_codGradoParlato text;
        p_record_cv_patente RECORD;
        p_record_cv_patentino RECORD;
        p_record_cv_albo RECORD;
        p_codPatente text;
        p_codPatentino text;
        p_codAlbo text;
        p_id_pf_principal_oper INTEGER;
        p_cod_area_titolo_const constant text := '32';
    begin
      /* dayLimit numero gg da sottrarre alla data corrente per recuperare  i CV da trasformare in nuovi CV */
      select (now() - concat(dayLimit, ' day')::interval) into var_dte ;
      select id_pf_principal into p_id_pf_principal_oper from mycas.pf_principal where cod_ruolo_portale = 'SYSTEM' and username = 'ut_modifiche_db';  
	    
	RAISE NOTICE 'Inizio recupero pregresso dal : %' , var_dte;
             
    FOR p_record_cv_dati_personali IN
      
       SELECT cv.id_cv_dati_personali 
	   FROM myportal.cv_dati_personali cv 	
	   WHERE cv.flag_eliminato <> 'Y' 
	     and cv.dtm_mod >  var_dte 
	     and COALESCE (flg_ido, 'N') <> 'Y'
	     and (cv.cod_provenienza_curriculum ='MYPORTAL' or cv.cod_provenienza_curriculum like 'SIL%')
    
    loop
    
    BEGIN
      p_data := current_timestamp;
     /*****************************************/
     /* aggiornamento tabella CvDatiPersoanli */
     /*****************************************/
      RAISE NOTICE 'Inizio processing  id_cv_dati_personali: %' , p_record_cv_dati_personali.id_cv_dati_personali;
	   UPDATE
               myportal.cv_dati_personali
        SET    flg_ido = 'Y',
               flag_trattamento_dati = 'Y',
               dtm_mod               = p_data,
               id_principal_mod      = p_id_pf_principal_oper,
               opz_tipo_decodifiche  = 'S'
        WHERE
               id_cv_dati_personali = p_record_cv_dati_personali.id_cv_dati_personali;
    
      /***************************************/
      /* aggiornamento tabella  CvIstruzioni */    
      /***************************************/
	   FOR p_record_cv_istruzione IN
	      
	    SELECT istr.id_cv_istruzione, istr.specifica, istr.num_anno , istr.cod_titolo
		FROM myportal.cv_istruzione	istr
		WHERE istr.id_cv_dati_personali =  p_record_cv_dati_personali.id_cv_dati_personali	
	    ORDER BY istr.id_cv_istruzione asc
	    
	    loop
	      p_specifica  := p_record_cv_istruzione.specifica;
	      p_cod_titolo := p_record_cv_istruzione.cod_titolo;
	   
	     IF (p_specifica is null) THEN
          
	       select count (1) into p_ret_istr from  mycas.de_titolo where de_titolo.cod_titolo = p_cod_titolo
              and  dt_fine_val >= now() AND dt_inizio_val <= now(); -- estraggo de_titolo validi
           
              if (p_ret_istr < 1) then -- il cod_titolo corrente non è valido quindi estraggo il padre
                select distinct cod_padre into p_cod_titolo_padre from  mycas.de_titolo where de_titolo.cod_titolo = p_cod_titolo;
                p_cod_titolo := p_cod_titolo_padre; -- ridefinisco la p_cod_titolo che ora corrisponde al padre
                select count (1) into p_ret_istr  from mycas.de_titolo where cod_titolo = p_cod_titolo_padre
                 and  dt_fine_val >= now() AND dt_inizio_val <= now();
              
               if (p_ret_istr < 1) then  -- se anche il cod titolo del padre è scaduto                      
                 RAISE EXCEPTION 'codTitolo % non trovato ', p_cod_titolo ;            
                 EXIT;
               end if;
	         end if;
	      
  	        select distinct descrizione into p_specifica from mycas.de_titolo where de_titolo.cod_titolo = p_cod_titolo;
			 			 
  	      END IF;
  	     
	      UPDATE
               myportal.cv_istruzione
           SET 
               dtm_mod               = p_data,
               id_principal_mod      = p_id_pf_principal_oper,
               flg_in_corso = 
			     CASE 
	  			   WHEN num_anno is null THEN true
 				   ELSE FALSE
		         end,
		         specifica = p_specifica,
		         cod_area_titolo_studio = p_cod_area_titolo_const
           WHERE
               id_cv_dati_personali = p_record_cv_dati_personali.id_cv_dati_personali 
               and id_cv_istruzione = p_record_cv_istruzione.id_cv_istruzione;
           END LOOP;
 
       /***************************************/
       /* aggiornamento tabella  CvEsperienze */    
       /***************************************/
      FOR p_record_cv_esperienze IN
	      
	    SELECT id_cv_esperienze_prof, cod_mansione_min, professione, da, a , cod_mansione  
		FROM myportal.cv_esperienze_prof 
		WHERE id_cv_dati_personali =  p_record_cv_dati_personali.id_cv_dati_personali	
	    ORDER BY id_cv_esperienze_prof asc
	     
	    loop
	 
	       p_cod_mansione_istat := p_record_cv_esperienze.cod_mansione_min;
         IF (p_cod_mansione_istat is null) THEN
        
            select distinct descrizione into p_professione  
              from myportal.de_mansione where cod_mansione  = p_record_cv_esperienze.cod_mansione;
            p_cod_mansione_istat := 'ND';
         
         else
        
            p_cod_mansione_istat := substring(p_cod_mansione_istat,1,4); 
            select distinct descrizione into p_professione  
               from myportal.de_mansione_min where cod_mansione_min  = p_record_cv_esperienze.cod_mansione_min;

          end if;
        
          UPDATE
               myportal.cv_esperienze_prof
           SET 
               dtm_mod               = p_data,
               id_principal_mod      = p_id_pf_principal_oper,
               cod_mansione_istat    = p_cod_mansione_istat,
               professione           = p_professione,
               flg_in_corso = 
			     CASE 
	  			   WHEN p_record_cv_esperienze.a is null THEN true
 				   ELSE FALSE
		         end,
  	           cod_contratto_sil = 
 				 CASE 
				   WHEN cod_contratto ='A00' THEN 'LP'
				   WHEN cod_contratto ='A01' THEN 'LT'
				   WHEN cod_contratto ='A02' THEN 'AP'
				   else null
				end
           WHERE
               id_cv_dati_personali = p_record_cv_dati_personali.id_cv_dati_personali 
               and id_cv_esperienze_prof = p_record_cv_esperienze.id_cv_esperienze_prof;
    	    END LOOP;
    
       /*******************************************/
       
       /****************************************/
       /* aggiornamento tabella  CvInformatica */    
       /****************************************/
    	   SELECT descrizione into p_descrizione   
		FROM myportal.cv_informatica
		WHERE id_cv_dati_personali =  p_record_cv_dati_personali.id_cv_dati_personali;	
       
   	     IF (p_descrizione is not null) THEN
          
	       UPDATE
               myportal.cv_informatica
           SET 
               dtm_mod               = p_data,
               id_principal_mod      = p_id_pf_principal_oper,
               descrizione = REGEXP_REPLACE( REGEXP_REPLACE(REGEXP_REPLACE(p_descrizione,'<[^>]+>','', 'g'), '(&nbsp;){1,}','','g' ), E'[\\n\\r]+' , ' ','g'  )
           WHERE
               id_cv_dati_personali = p_record_cv_dati_personali.id_cv_dati_personali;
              
        end if;

       /****************************************/
       /* aggiornamento tabella  CvCompetenzeTrasv */    
       /****************************************/	 
        FOR p_record_cv_competenze_trasv in
        
	    SELECT cap_comp_rel_interpersonali , cap_comp_tecniche , cap_comp_organizzative
		FROM myportal.cv_competenze_trasv
		WHERE id_cv_dati_personali =  p_record_cv_dati_personali.id_cv_dati_personali
    	 ORDER BY id_cv_competenze_trasv asc
	    
	    loop
          p_cap_comp_rel_interpersonali := p_record_cv_competenze_trasv.cap_comp_rel_interpersonali;
		  p_cap_comp_tecniche := p_record_cv_competenze_trasv.cap_comp_tecniche;
		  p_cap_comp_organizzative := p_record_cv_competenze_trasv.cap_comp_organizzative;
		
         
   	     IF (p_cap_comp_rel_interpersonali is not null or p_cap_comp_tecniche is not null or p_cap_comp_organizzative is not null) THEN
          
	       UPDATE
               myportal.cv_competenze_trasv
           SET 
               dtm_mod               = p_data,
               id_principal_mod      = p_id_pf_principal_oper,-- verificare se utente 0 è admin se non mettere admin
               cap_comp_rel_interpersonali =
                 CASE 
	  			   WHEN p_cap_comp_rel_interpersonali is not null THEN  REGEXP_REPLACE( REGEXP_REPLACE(REGEXP_REPLACE(p_cap_comp_rel_interpersonali,'<[^>]+>','', 'g'), '(&nbsp;){1,}','','g' ), E'[\\n\\r]+' , ' ','g'  )
 				   ELSE null
		         end,
