CREATE FUNCTION myportal.yg_conferimento_stato_passo3(cod_regione_portale character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    count_processed integer;
    codice_fiscale_param myportal.yg_conferimento_stato.codice_fiscale%TYPE;
    yg_conferimento_stato_row myportal.yg_conferimento_stato%ROWTYPE;
    yg_conferimento_stato_curs CURSOR IS SELECT codice_fiscale FROM myportal.yg_conferimento_stato;
    yg_adesione_curs_row myportal.yg_adesione%ROWTYPE;
    yg_adesione_curs CURSOR (codice_fiscale_param varchar) IS SELECT cod_mono_prov, id_yg_adesione
                                                              FROM myportal.yg_adesione
                                                              WHERE flg_adesione = 'Y'
                                                                AND cod_regione_rif_notifica = cod_regione_portale                                                                                                                                         AND codice_fiscale = codice_fiscale_param;
    count_adesioni integer;
    id_yg_adesione_selected myportal.yg_adesione.id_yg_adesione%TYPE;
BEGIN
    count_processed := 0;

    -- Ciclo su ogni CF presente in yg_conferimento_stato
    FOR yg_conferimento_stato_row IN yg_conferimento_stato_curs LOOP
        codice_fiscale_param := yg_conferimento_stato_row.codice_fiscale;
        id_yg_adesione_selected := null;
        RAISE NOTICE 'yg_conferimento_stato.codice_fiscale = %', codice_fiscale_param;

        -- Conto le adesioni con il CF dato
        SELECT count(1) INTO count_adesioni
        FROM myportal.yg_adesione
        WHERE flg_adesione = 'Y'
          AND cod_regione_rif_notifica = cod_regione_portale
          AND codice_fiscale = codice_fiscale_param;

        -- Se ho una sola adesione con il CF indicato prendo quella, se ne ho di piu' prendo quella generata internamente (cod_mono_prov = 'I')
        IF count_adesioni = 1 THEN
             SELECT * INTO yg_adesione_curs_row
             FROM myportal.yg_adesione
             WHERE flg_adesione = 'Y'
               AND cod_regione_rif_notifica = cod_regione_portale
               AND codice_fiscale = codice_fiscale_param;

             id_yg_adesione_selected := yg_adesione_curs_row.id_yg_adesione;
             RAISE NOTICE 'cf = %, yg_adesione.id_yg_adesione = %, yg_adesione.cod_mono_prov = %', codice_fiscale_param, yg_adesione_curs_row.id_yg_adesione, yg_adesione_curs_row.cod_mono_prov;
        ELSE
            FOR yg_adesione_curs_row IN yg_adesione_curs(codice_fiscale_param) LOOP
                IF yg_adesione_curs_row.cod_mono_prov = 'I' THEN
                    id_yg_adesione_selected := yg_adesione_curs_row.id_yg_adesione;
                END IF;
                RAISE NOTICE 'cf = %, yg_adesione.id_yg_adesione = %, yg_adesione.cod_mono_prov = %', codice_fiscale_param, yg_adesione_curs_row.id_yg_adesione, yg_adesione_curs_row.cod_mono_prov;
            END LOOP;
        END IF;

        RAISE NOTICE 'cf = %, id_yg_adesione_selected = %', codice_fiscale_param, id_yg_adesione_selected;

        -- Associo l'id_yg_adesione selezionato
        UPDATE myportal.yg_conferimento_stato SET id_yg_adesione = id_yg_adesione_selected WHERE CURRENT OF yg_conferimento_stato_curs;

        -- Aggiorno il conteggio dei record elaborati
        count_processed := count_processed + 1;
    END LOOP;

    RETURN count_processed;
END;
$$;


