--

CREATE FUNCTION myportal.yg_conferimento_stato_passo4(cod_regione_portale character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    count_processed integer;
    codice_fiscale_param myportal.yg_conferimento_stato.codice_fiscale%TYPE;
    yg_conferimento_stato_row myportal.yg_conferimento_stato%ROWTYPE;
    yg_comferimento_doppi_curs CURSOR IS SELECT codice_fiscale, count(codice_fiscale)
                                         FROM myportal.yg_conferimento_stato
                                         GROUP BY codice_fiscale
                                         HAVING count(codice_fiscale) > 1;
    yg_conferimento_stato_p_row myportal.yg_conferimento_stato%ROWTYPE;
    yg_conferimento_stato_c_row myportal.yg_conferimento_stato%ROWTYPE;
    yg_conferimento_stato_altri_row myportal.yg_conferimento_stato%ROWTYPE;
    yg_conferimento_stato_doppio_row myportal.yg_conferimento_stato%ROWTYPE;
    yg_comferimento_annullare_curs CURSOR (codice_fiscale_param varchar) IS SELECT id_yg_conferimento_stato, codice_fiscale, cod_stato_adesione_min
                                                                        FROM myportal.yg_conferimento_stato
                                                                        WHERE codice_fiscale = codice_fiscale_param;
BEGIN
    count_processed := 0;

    -- Ciclo su ogni CF doppio presente in yg_conferimento_stato
    FOR yg_conferimento_stato_row IN yg_comferimento_doppi_curs LOOP
        codice_fiscale_param := yg_conferimento_stato_row.codice_fiscale;
        yg_conferimento_stato_p_row := null;
        yg_conferimento_stato_c_row := null;

        SELECT id_yg_conferimento_stato, codice_fiscale, cod_stato_adesione_min INTO yg_conferimento_stato_p_row FROM myportal.yg_conferimento_stato WHERE cod_stato_adesione_min = 'P' AND codice_fiscale = codice_fiscale_param;
        SELECT id_yg_conferimento_stato, codice_fiscale, cod_stato_adesione_min INTO yg_conferimento_stato_c_row FROM myportal.yg_conferimento_stato WHERE cod_stato_adesione_min = 'C' AND codice_fiscale = codice_fiscale_param;
        SELECT id_yg_conferimento_stato, codice_fiscale, cod_stato_adesione_min INTO yg_conferimento_stato_altri_row FROM myportal.yg_conferimento_stato WHERE cod_stato_adesione_min NOT IN ('P', 'C') AND codice_fiscale = codice_fiscale_param;

        -- se ho un'adesione in stato P e una in stato C, elaboro solo quella in stato P
        IF yg_conferimento_stato_p_row.id_yg_conferimento_stato IS NOT NULL 
             AND yg_conferimento_stato_c_row.id_yg_conferimento_stato IS NOT NULL
             AND yg_conferimento_stato_altri_row.id_yg_conferimento_stato IS NULL
            THEN
            UPDATE myportal.yg_conferimento_stato SET flg_processato = 'N' WHERE id_yg_conferimento_stato = yg_conferimento_stato_c_row.id_yg_conferimento_stato;
            RAISE NOTICE 'cf = %, id_yg_conferimento_stato = %, cod_stato_adesione_min = P, flg_processato = NULL', codice_fiscale_param, yg_conferimento_stato_p_row.id_yg_conferimento_stato;
            RAISE NOTICE 'cf = %, id_yg_conferimento_stato = %, cod_stato_adesione_min = C, flg_processato = N', codice_fiscale_param, yg_conferimento_stato_c_row.id_yg_conferimento_stato;
        ELSE
            -- in tutti gli altri casi non elaboro alcuna adesione
            FOR yg_conferimento_stato_doppio_row IN yg_comferimento_annullare_curs(codice_fiscale_param) LOOP
                UPDATE myportal.yg_conferimento_stato SET flg_processato = 'N' WHERE CURRENT OF yg_comferimento_annullare_curs;
                RAISE NOTICE 'cf = %, id_yg_conferimento_stato = %, cod_stato_adesione_min = %, flg_processato = N', codice_fiscale_param, yg_conferimento_stato_doppio_row.id_yg_conferimento_stato, yg_conferimento_stato_doppio_row.cod_stato_adesione_min;
            END LOOP;
        END IF;

        -- Aggiorno il conteggio dei record elaborati
        count_processed := count_processed + 1;
    END LOOP;

    RETURN count_processed;
END;
$$;


