      RAISE NOTICE 'Record processati: %', p_count;
    END IF;

    -------------------------------------
    
    update myportal.utente_info
    set cod_com_nascita = substring(p_record_yg_adesione.codice_fiscale, 12, 4),
        dtm_mod = current_date,
        id_principal_mod = 0
    where id_pf_principal = p_record_yg_adesione.id_pf_principal;
    
    -------------------------------------

  END LOOP;

  RAISE NOTICE 'Record processati: %', p_count;
  RAISE NOTICE 'Fine yg_fix_comune_nascita_adesioni_cooperazione (YG)';
  
