	FROM myportal.yg_adesione adesione
	INNER JOIN myportal.utente_info utente on utente.id_pf_principal = adesione.id_pf_principal
	WHERE 
	  utente.cod_provincia is not null
	  AND (
		adesione.cod_provincia_rif_notifica is null or 
		(
		  adesione.cod_provincia_rif_notifica is not null and
		  adesione.dt_presa_in_carico is null and 
		  adesione.cod_provincia_rif_notifica != utente.cod_provincia
		  -- nuovo pezzo
		  and utente.cod_provincia in (
		   		select cod_provincia
  				from myportal.de_provincia
  				where cod_regione = in_cod_regione_portale
  		  )
		)
	  )
	  AND adesione.cod_regione_rif_notifica = in_cod_regione_portale
	ORDER BY adesione.id_pf_principal ASC
	
  LOOP

    -- conta i record processati
    p_count := p_count + 1;

    IF (p_count % 1000) = 0 THEN
      RAISE NOTICE 'Record processati: %', p_count;
    END IF;

